const reactions = s.docs.map(d => d.data())
                    .sort((a, b) => b.time.seconds - a.time.seconds || (b.time.nanoseconds - a.time.nanoseconds))
                    .reduce((a, r) => {
                        if (!a[r.uid]) {
                            a[r.uid] = r;
                        }
                        return a;
                    }, {});

                state.reactions = Object.values(reactions);

